package com.pichincha.movement.configuration;

import java.time.Instant;
import java.util.HashMap;
import java.util.Map;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.method.configuration.EnableReactiveMethodSecurity;
import org.springframework.security.config.web.server.ServerHttpSecurity;
import org.springframework.security.oauth2.jwt.Jwt;
import org.springframework.security.oauth2.jwt.ReactiveJwtDecoder;
import org.springframework.security.web.server.SecurityWebFilterChain;

import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.security.Keys;
import reactor.core.publisher.Mono;

@Configuration
@EnableReactiveMethodSecurity
public class SecurityConfig {

  @Value("${jwt.secret:mySecretKey}")
  private String jwtSecret;

  @Bean
  public SecurityWebFilterChain springSecurityFilterChain(ServerHttpSecurity http) {
    return http
        .csrf(csrf -> csrf.disable())
        .authorizeExchange(exchange -> exchange
            .pathMatchers("/api/auth/**", "/webjars/**", "/actuator/**",
                "/swagger-ui/**", "/v3/api-docs/**")
            .permitAll()
            .anyExchange()
            .authenticated())
        .oauth2ResourceServer(oauth2 -> oauth2
            .jwt(jwt -> jwt.jwtDecoder(jwtDecoder())))
        .build();
  }

  @Bean
  public ReactiveJwtDecoder jwtDecoder() {
    return token -> {
      if (token == null || token.isBlank()) {
        return Mono.error(new RuntimeException("JWT token is missing or empty"));
      }
      try {
        var claimsJws = Jwts.parser()
            .setSigningKey(Keys.hmacShaKeyFor(jwtSecret.getBytes()))
            .parseClaimsJws(token);
        Map<String, Object> claims = new HashMap<>(claimsJws.getBody());
        Jwt jwt = new Jwt(
            token,
            Instant.ofEpochMilli(claimsJws.getBody().getIssuedAt().getTime()),
            Instant.ofEpochMilli(claimsJws.getBody().getExpiration().getTime()),
            Map.of("alg", "HS256"),
            claims
        );
        return Mono.just(jwt);
      } catch (Exception e) {
        return Mono.error(new RuntimeException("Invalid JWT", e));
      }
    };
  }
}

package com.pichincha.movement.configuration;

import java.time.Instant;
import java.util.HashMap;
import java.util.Map;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.http.HttpMethod;
import org.springframework.security.config.annotation.method.configuration.EnableReactiveMethodSecurity;
import org.springframework.security.config.web.server.ServerHttpSecurity;
import org.springframework.security.oauth2.jwt.Jwt;
import org.springframework.security.oauth2.jwt.ReactiveJwtDecoder;
import org.springframework.security.web.server.SecurityWebFilterChain;
import org.springframework.web.cors.CorsConfiguration;
import org.springframework.web.cors.reactive.CorsConfigurationSource;
import org.springframework.web.cors.reactive.UrlBasedCorsConfigurationSource;

import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.security.Keys;
import reactor.core.publisher.Mono;

@Configuration
@EnableReactiveMethodSecurity
public class SecurityConfig {

  @Value("${jwt.secret:mySecretKey}")
  private String jwtSecret;

  @Bean
  public SecurityWebFilterChain springSecurityFilterChain(ServerHttpSecurity http) {
    return http
        .csrf(csrf -> csrf.disable())
        .cors(cors -> cors.configurationSource(corsConfigurationSource()))
        .authorizeExchange(exchange -> exchange
            .pathMatchers(HttpMethod.OPTIONS, "/**").permitAll()
            .pathMatchers("/api/auth/**", "/webjars/**", "/actuator/**", 
                "/swagger-ui/**", "/v3/api-docs/**")
            .permitAll()
            .anyExchange()
            .authenticated())
        .oauth2ResourceServer(oauth2 -> oauth2
            .jwt(jwt -> jwt.jwtDecoder(jwtDecoder())))
        .build();
  }

  @Bean
  public CorsConfigurationSource corsConfigurationSource() {
    CorsConfiguration configuration = new CorsConfiguration();
    configuration.setAllowCredentials(true);
    configuration.addAllowedOriginPattern("*");
    configuration.addAllowedMethod("*");
    configuration.addAllowedHeader("*");
    configuration.addExposedHeader("x-app");
    configuration.addExposedHeader("x-guid");
    configuration.addExposedHeader("x-channel");
    configuration.addExposedHeader("x-medium");
    configuration.addExposedHeader("x-session");

    UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
    source.registerCorsConfiguration("/**", configuration);
    return source;
  }

  @Bean
  public ReactiveJwtDecoder jwtDecoder() {
    return token -> {
      if (token == null || token.isBlank()) {
        return Mono.error(new RuntimeException("JWT token is missing or empty"));
      }
      try {
        var claimsJws = Jwts.parserBuilder()
            .setSigningKey(Keys.hmacShaKeyFor(jwtSecret.getBytes()))
            .build()
            .parseClaimsJws(token);
        Map<String, Object> claims = new HashMap<>(claimsJws.getBody());
        Jwt jwt = new Jwt(
            token,
            Instant.ofEpochMilli(claimsJws.getBody().getIssuedAt().getTime()),
            Instant.ofEpochMilli(claimsJws.getBody().getExpiration().getTime()),
            Map.of("alg", "HS256"),
            claims
        );
        return Mono.just(jwt);
      } catch (Exception e) {
        return Mono.error(new RuntimeException("Invalid JWT", e));
      }
    };
  }
}

package com.pichincha.movement.domain;

import java.util.Map;

public record LoginResponse(
  String token,
  String refreshToken,
  long expiresIn,
  Map<String, Object> user
) {
}

package com.pichincha.movement.infrastructure.input.adapter.rest;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.http.MediaType;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.SignatureAlgorithm;
import io.jsonwebtoken.security.Keys;
import reactor.core.publisher.Mono;

import java.util.Date;
import java.util.Map;

@RestController
@RequestMapping("/api/auth")
public class AuthController {

	@Value("${jwt.secret:mySecretKey}")
	private String jwtSecret;

	@Value("${jwt.username:username}")
	private String username;

	@Value("${jwt.password:password}")
	private String password;

	@GetMapping("/ping")
	public Mono<String> ping() {
		return Mono.just("API est치 funcionando sin autenticaci칩n.");
	}

	@PreAuthorize("hasRole('ADMIN')")
	@GetMapping("/admin")
	public Mono<String> adminOnlyAccess() {
		return Mono.just("Acceso concedido al ADMIN");
	}

	@PostMapping(value = "/login", consumes = MediaType.APPLICATION_JSON_VALUE)
	public Mono<String> login(@RequestBody Map<String, String> credentials) {
		String username = credentials.get("username");
		String password = credentials.get("password");
		if (username.equals(this.username) && password.equals(this.password)) {
			String token = Jwts.builder().setSubject(username).claim("roles", "ROLE_ADMIN").setIssuedAt(new Date())
					.setExpiration(new Date(System.currentTimeMillis() + 3600000))
					.signWith(Keys.hmacShaKeyFor(jwtSecret.getBytes()), SignatureAlgorithm.HS256).compact();
			return Mono.just(token);
		}
		return Mono.error(new RuntimeException("Invalid credentials"));
	}
}

package com.pichincha.movement.infrastructure.input.adapter.rest;

import com.pichincha.movement.domain.LoginResponse;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.http.MediaType;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import io.jsonwebtoken.Jwts;
import io.jsonwebtoken.SignatureAlgorithm;
import io.jsonwebtoken.security.Keys;
import reactor.core.publisher.Mono;

import java.util.Date;
import java.util.Map;
import java.util.HashMap;

@RestController
@RequestMapping("/api/auth")
public class AuthController {

	@Value("${jwt.secret:mySecretKey}")
	private String jwtSecret;

	@Value("${jwt.username:username}")
	private String username;

	@Value("${jwt.password:password}")
	private String password;

	@GetMapping("/ping")
	public Mono<String> ping() {
		return Mono.just("API est치 funcionando sin autenticaci칩n.");
	}

	@PreAuthorize("hasRole('ADMIN')")
	@GetMapping("/admin")
	public Mono<String> adminOnlyAccess() {
		return Mono.just("Acceso concedido al ADMIN");
	}

	@PostMapping(value = "/login", consumes = MediaType.APPLICATION_JSON_VALUE)
	public Mono<LoginResponse> login(@RequestBody Map<String, String> credentials) {
		String username = credentials.get("username");
		String password = credentials.get("password");
		if (username.equals(this.username) && password.equals(this.password)) {
			String token = Jwts.builder().setSubject(username).claim("roles", "ROLE_ADMIN").setIssuedAt(new Date())
					.setExpiration(new Date(System.currentTimeMillis() + 3600000))
					.signWith(Keys.hmacShaKeyFor(jwtSecret.getBytes()), SignatureAlgorithm.HS256).compact();

			String refreshToken = Jwts.builder().setSubject(username).setIssuedAt(new Date())
					.setExpiration(new Date(System.currentTimeMillis() + 7200000))
					.signWith(Keys.hmacShaKeyFor(jwtSecret.getBytes()), SignatureAlgorithm.HS256).compact();

			Map<String, Object> user = new HashMap<>();
			user.put("username", username);
			user.put("roles", "ROLE_ADMIN");

			LoginResponse response = new LoginResponse(token, refreshToken, 3600, user);
			return Mono.just(response);
		}
		return Mono.error(new RuntimeException("Invalid credentials"));
	}
}
